    int find_floor(std::string_view instr) {
        int retval = 0;
        while (!instr.empty()) {
            retval += -(2 * static_cast<int>(instr.front() - '(')) + 1;
            instr.remove_prefix(1);
        }
        return retval;
    }

    int find_basement_trip(std::string_view instr) {
        int floor = 0, num = 1;
        while (!instr.empty()) {
            floor += -(2 * static_cast<int>(instr.front() - '(')) + 1;
            if (floor < 0) {
                break;
            }
            instr.remove_prefix(1);
            ++num;
        }
        return num;
    }

