    std::string part_1(const std::vector<std::string>& lines) {
        return std::to_string(find_floor(INPUT));
    }

    /************************* Part 2 *************************/
    std::string part_2(const std::vector<std::string>& lines) {
        return std::to_string(find_basement_trip(INPUT));
    }

